
def search_for_patterns(genome, k, L, t):
    indexes, result = {}, []
    for current in range(len(genome) + 1 - k):
        part = genome[current:current + k]
        if part not in indexes:
            indexes[part] = []
        while len(indexes[part]) and current + k - indexes[part][0] > L:
            indexes[part] = indexes[part][1:]
        indexes[part].append(current)
        if len(indexes[part]) == t and part not in result:
            result.append(part)
    return result


def test(genome, k, L, t, result):
    return sorted(search_for_patterns(genome, k, L, t)) == sorted(result)

