def hamming_distance(a, b):
    if len(a) != len(b):
        raise Exception("len(a) != len(b)")
    return len(list(filter(lambda item: item[0] != item[1], zip(a, b))))


def approx_pattern_matching(pattern, text, d):
    p_len = len(pattern)
    distances = [hamming_distance(pattern, text[i:i + p_len])
                 for i in range(0, len(text) - p_len + 1)]
    return [str(item[0]) for item in filter(lambda item: item[1] <= d, zip(range(len(distances)), distances))]


pattern = "TTCCGTACCTGG"
text = ""
d = 6


print(" ".join(approx_pattern_matching(pattern, text, d)))
